INSERT INTO tb_tourist_spot (name, description, img_url, city_id) VALUES ('Marco zero', 'O Marco Zero foi onde a cidade literalmente começou. Localizado na Praça Rio Branco, é lá que se encontra o ponto inicial para a contagem das distâncias da cidade.', 'www.marcozerto.braziltours.br', 1)
INSERT INTO tb_tourist_spot (name, description, img_url, city_id) VALUES ('Praia de Boa Viagem', 'A Praia de Boa Viagem é a mais famosa da cidade, sempre muito movimentada e frequentada por locais e turistas.', 'www.praiadeboaviagem.braziltours.br', 1)
INSERT INTO tb_tourist_spot (name, description, img_url, city_id) VALUES ('Pôr do sol do Jacaré', 'A praia do Jacaré está localizada em Cabedelo, a cerca de 20 minutos de carro de Tambaú. No momento do sol se pôr, o Jurandy do Sax passa de barco tocando o Bolero de Ravel.', 'www.pordosoldojacare.braziltours.br', 2)
INSERT INTO tb_tourist_spot (name, description, img_url, city_id) VALUES ('Skybar', 'Localizado a 145 metros de altura e no 44º andar do Complexo Tour Geneve, ele é apenas o bar mais alto do Nordeste.', 'www.skybar.braziltours.br', 2)
INSERT INTO tb_tourist_spot (name, description, img_url, city_id) VALUES ('Praia do Francês', 'Pode ser considerada a mais charmosa praia de Alagoas. O bairro, totalmente projetado, tem uma região dedicada aos restaurantes, que vão de hambúrgueres artesanais a altas receitas exclusivas da culinária mediterrânea.', 'www.praiadofrances.braziltours.br', 3)
INSERT INTO tb_tourist_spot (name, description, img_url, city_id) VALUES ('Ponta Verde', 'Ponta Verde é um dos destinos mais procurados por quem gosta do estilo praia com orla e muito agito, tanto de dia quanto de noite. A praia traz uma sensação de cidade grande mesclada com uma natureza impecável, o que a torna a mais valorizada da região.', 'www.pontaverde.braziltours.br', 3)
INSERT INTO tb_tourist_spot (name, description, img_url, city_id) VALUES ('Feira de Caruaru', 'Considerada uma das maiores feiras ao ar livre do país, a Feira de Caruaru atrai pessoas de todo o Nordeste brasileiro. Ficou famosa ao ser cantada por Luiz Gonzaga, na música de autoria de Onildo Almeida.', 'www.feiradecaruaru.braziltours.br', 4)
INSERT INTO tb_tourist_spot (name, description, img_url, city_id) VALUES ('Alto do Moura', 'Hoje, o local representa um dos mais significativos centros de artes figurativas das Américas.', 'www.altodomoura.braziltours.br', 4)